sys_processor.script:
(function process(g_request, g_response, g_processor) {

	// Add your code here
	var operation = g_request.getParameter("operation");
	var sys_id = g_request.getParameter("blog_sys_id");
	var column = g_request.getParameter("column");
	var value = g_request.getParameter("value");

	g_response.setContentType("text/html;charset=UTF-8");
	//g_processor.writeOutput("<iframe src='http://demo.testfire.net' height='600px' width='600px'></iframe>");

	var blog = new GlideRecord("sn_blog_management_blog_post");
	if(blog.get(sys_id))
		{
			if(operation == "read")
				{
					g_processor.writeOutput(blog.getValue(column));
				}
			else if(operation == "update")
				{
					blog.setValue(column,value);
					g_processor.writeOutput(column +" has been updated to "+value);
				}
			else{
				g_processor.writeOutput("Not Found!");
			}
		}


})(g_request, g_response, g_processor);
