sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
	var writer = response.getStreamWriter(),
        hdrs = {},
        table = 'sn_blog_management_blog_post',
        record_limit = 100,
        now_GR = new GlideRecordSecure(table);


    hdrs['Content-Type'] = 'application/json';
    response.setStatus(200);
    response.setHeaders(hdrs);

    now_GR.setLimit(record_limit);
    now_GR.query();

	//  start building response object
    writer.writeString("{\"results\":[");


    //  iterate over incident records and build JSON representations to be streamed out.
    while (now_GR.next()) {
        var blogObj = {};

        blogObj.title = now_GR.title + '';
        blogObj.body = now_GR.body + '';
        writer.writeString(global.JSON.stringify(blogObj));

        if (now_GR.hasNext()) {
            writer.writeString(",");
        }
    }

    //  close the response object
    writer.writeString("]}");

})(request, response);
